import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { UserService } from 'src/services/user.service';
import { RegisterRequestDto } from 'src/Models/RegisterRequestDto';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {

  name: string;
  surname: string;
  email: string;
  phone: string;
  password: string;
  photo: File;

  constructor(
    private userService: UserService
  ) {}

  onFileChange(event: any) {
    // Handle file input change logic here
    const fileList: FileList = event.target.files;
    if (fileList.length > 0) {
      this.photo = fileList[0];
    }
  }

  register(){
    let model: RegisterRequestDto =  {
      name: this.name,
      surname: this.surname,
      phone: this.phone,
      email: this.email,
      password: this.password,
      photo: this.photo
    }

    this.userService.register(model).subscribe(
      (response) => { alert(response) },
      (error) => { console.log(error); });
  }


}
<form #registrationForm="ngForm" (ngSubmit)="register()" class="container mt-5">
  <h2 class="mb-4">Register</h2>

  <div class="form-row">
    <div class="form-group col-md-6">
      <label for="name">Name:</label>
      <input type="text" id="name" name="name" [(ngModel)]="name" class="form-control" required>
    </div>

    <div class="form-group col-md-6">
      <label for="surname">Surname:</label>
      <input type="text" id="surname" name="surname" [(ngModel)]="surname" class="form-control" required>
    </div>
  </div>

  <div class="form-group col-md-6">
    <label for="email">Email:</label>
    <input type="email" id="email" name="email" [(ngModel)]="email" class="form-control" required>
  </div>

  <div class="form-group col-md-6">
    <label for="phone">Phone:</label>
    <input type="tel" id="phone" name="phone" [(ngModel)]="phone" class="form-control" required>
  </div>

  <div class="form-group col-md-6">
    <label for="password">Password:</label>
    <input type="password" id="password" name="password" [(ngModel)]="password" class="form-control" required>
  </div>

  <div class="form-group" style="margin-top: 15px;">
    <label for="photo">Profile Photo: </label>
    <input type="file" id="photo" name="photo" (change)="onFileChange($event)" class="form-control-file">
  </div>

  <button type="submit" class="btn btn-primary" [disabled]="!registrationForm.valid">Register</button>
</form>
