import { Component } from '@angular/core';
import { Meeting } from 'src/Models/Meeting';
import { MeetingService } from 'src/services/meeting.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent {
  meetings: Meeting[] = [];
  currentMeeting: Meeting = new Meeting();
  userId: number;
  isEditing = false;

  constructor(
    private meetingService: MeetingService,
  ) {
    let localUserId = localStorage.getItem('userId');

    if (localUserId !== null && localUserId !== undefined) {
      this.userId = +localUserId;
      this.getAll(this.userId);
    }
  }

  getAll(userId: number) {
    this.meetingService.getAll(userId).subscribe(
      (response) => {
        this.meetings = response as Meeting[];
      },
      (error) => {
        console.log(error);
      }
    );
  }

  editMeeting(meeting: Meeting) {
    this.isEditing = true;
    this.currentMeeting = { ...meeting };
  }

  deleteMeeting(meetingId: number) {
    this.meetingService.delete(meetingId).subscribe(
      () => {
        this.getAll(this.userId);
      },
      (error) => {
        console.log(error);
      }
    );
  }

  saveMeeting(){
    if(this.isEditing){
      this.updateMeeting(this.currentMeeting);
      return;
    }

    this.createMeeting(this.currentMeeting);
  }

  updateMeeting(meeting: Meeting) {
    this.meetingService.update(meeting).subscribe(
      () => {
        this.getAll(this.userId);
      },
      (error) => {
        console.log(error);
      }
    );
  }

  createMeeting(meeting: Meeting) {
    this.meetingService.create(meeting).subscribe(
      () => {
        this.getAll(this.userId);
      },
      (error) => {
        console.log(error);
      }
    );
  }
}
<div class="container">
  <h1 class="mb-4">Meetings</h1>

  <ul>
    <li *ngFor="let meeting of meetings">
      <div class="d-flex justify-content-between">
        <div>
          <h4>{{ meeting.name }}</h4>
          <p>{{ meeting.startDate | date }} to {{ meeting.endDate | date }}</p>
          <p>{{ meeting.description }}</p>
        </div>
        <div>
          <button class="btn btn-primary" (click)="editMeeting(meeting)">
            Edit
          </button>
          <button class="btn btn-danger" (click)="deleteMeeting(meeting.id)">
            Delete
          </button>
        </div>
      </div>
    </li>
  </ul>

  <h2 class="mt-4">{{ isEditing ? "Edit" : "Add" }} Meeting</h2>
  <form (submit)="saveMeeting()">
    <div class="form-group">
      <label for="name">Meeting Name:</label>
      <input
        type="text"
        id="name"
        class="form-control"
        [(ngModel)]="currentMeeting.name"
        [ngModelOptions]="{ standalone: true }"
        required
      />
    </div>

    <div class="form-group">
      <label for="startDate">Start Date:</label>
      <input
        type="datetime-local"
        id="startDate"
        class="form-control"
        [(ngModel)]="currentMeeting.startDate"
        [ngModelOptions]="{ standalone: true }"
        required
      />
    </div>

    <div class="form-group">
      <label for="endDate">End Date:</label>
      <input
        type="datetime-local"
        id="endDate"
        class="form-control"
        [(ngModel)]="currentMeeting.endDate"
        [ngModelOptions]="{ standalone: true }"
        required
      />
    </div>

    <div class="form-group">
      <label for="description">Description:</label>
      <textarea
        id="description"
        class="form-control"
        [(ngModel)]="currentMeeting.description"
        [ngModelOptions]="{ standalone: true }"
      ></textarea>
    </div>

    <div class="form-group">
      <label for="document">Document:</label>
      <input
        type="file"
        id="document"
        class="form-control"
        [(ngModel)]="currentMeeting.document"
        [ngModelOptions]="{ standalone: true }"
      />
    </div>

    <button type="submit" class="btn btn-success">
      {{ isEditing ? "Update" : "Add" }} Meeting
    </button>
  </form>
</div>
