  }

  updateMeeting(meeting: Meeting){
    this.meetingService.edit(meeting).subscribe(
      (response) => 
      {
         this.meetings = response as Meeting[];
         console.log(this.meetings);
        },
      (error) => { console.log(error); });
  }
}
