import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {
  public forecasts: WeatherForecast[] = [];

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<WeatherForecast[]>(baseUrl + 'weatherforecast').subscribe(result => {
      this.forecasts = result;
    }, error => console.error(error));
  }
}

interface WeatherForecast {
  date: string;
  temperatureC: number;
  temperatureF: number;
  summary: string;
}

<form #registrationForm="ngForm" class="container mt-5">
  <h2 class="mb-4">Kayıt Formu</h2>

  <div class="form-row">
    <div class="form-group col-md-6">
      <label for="ad">Ad:</label>
      <input type="text" id="ad" name="ad"  class="form-control" required>
    </div>

    <div class="form-group col-md-6">
      <label for="soyad">Soyad:</label>
      <input type="text" id="soyad" name="soyad" class="form-control" required>
    </div>
  </div>

  <div class="form-group">
    <label for="email">Email:</label>
    <input type="email" id="email" name="email"  class="form-control" required>
  </div>

  <div class="form-group">
    <label for="telefon">Telefon:</label>
    <input type="tel" id="telefon" name="telefon"  class="form-control" required>
  </div>

  <div class="form-group">
    <label for="sifre">Şifre:</label>
    <input type="password" id="sifre" name="sifre" class="form-control" required>
  </div>

  <div class="form-group" style="margin-top: 15px;">
    <label for="profilResmi">Profil Resmi Yükle:</label>
    <input type="file" id="profilResmi" name="profilResmi"  class="form-control-file">
  </div>

  <button type="submit" class="btn btn-primary" [disabled]="!registrationForm.valid">Kayıt Ol</button>
</form>
